mapper.select:
SELECT p.product_id AS id, ( SELECT pi.url FROM product_image pi WHERE pi.product_id = p.product_id AND ROWNUM = 1 ) AS image, b.name AS brand, c.main_name AS categoryMain, c.sub_name AS categorySub, p.grade AS grade, TO_CHAR(p.updated_at, 'YYYY-MM-DD HH24:MI:SS') AS finishDate FROM product p JOIN brand b ON p.brand_id = b.brand_id JOIN category c ON p.category_id = c.category_id WHERE p.status = 'FINISH'
AND b.name = #{brand}
AND c.main_name = #{categoryMain}
AND c.sub_name = #{categorySub}
AND p.grade = #{grade}
ORDER BY p.updated_at DESC OFFSET #{pageStart} ROWS FETCH NEXT #{offset} ROWS ONLY

SELECT COUNT(*)
FROM product p
JOIN brand b ON p.brand_id = b.brand_id
JOIN category c ON p.category_id = c.category_id
WHERE p.status = 'FINISH'

    AND b.name = #{brand}


    AND c.main_name = #{categoryMain}


    AND c.sub_name = #{categorySub}


    AND p.grade = #{grade}

SELECT p.product_id AS id, ( SELECT pi.url FROM product_image pi WHERE pi.product_id = p.product_id AND ROWNUM = 1 ) AS image, b.name AS brand, c.main_name AS categoryMain, c.sub_name AS categorySub, p.grade AS grade, p.paid_point AS paid_point, p.status AS status, TO_CHAR(p.updated_at, 'YYYY-MM-DD HH24:MI:SS') AS updatedAt FROM product p JOIN brand b ON p.brand_id = b.brand_id JOIN category c ON p.category_id = c.category_id WHERE 1 = 1
AND b.name = #{brand}
AND c.main_name = #{categoryMain}
AND c.sub_name = #{categorySub}
AND p.grade = #{grade}
AND p.status IN
#{status}
ORDER BY p.updated_at DESC OFFSET #{pageStart} ROWS FETCH NEXT #{offset} ROWS ONLY

SELECT COUNT(*)
FROM product p
JOIN brand b ON p.brand_id = b.brand_id
JOIN category c ON p.category_id = c.category_id
WHERE 1 = 1

        AND b.name = #{brand}


        AND c.main_name = #{categoryMain}


        AND c.sub_name = #{categorySub}


        AND p.grade = #{grade}


        AND p.status IN

            #{status}

SELECT grade
FROM product
WHERE product_id = #{productId}

SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
FROM product
WHERE product_id = #{productId}

SELECT status
FROM product
WHERE product_id = #{productId}
